package y_p;

import java.util.Scanner;

public class y_1301 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner cin = new Scanner(System.in);
		while (cin.hasNext()) {
			int x = cin.nextInt();
			System.out.println(x * x * x);
		}
		cin.close();
	}

}
